#include<iostream>

using namespace std;

const int N = 1e6 + 10; // + 10
int n;
int a[N];

void quick_sort(int * a, int l, int r) //lleftrright
{
    if (l >= r) return;

    /**
    * l + r >> 1(l + r) / 2
    * 
    * 
    * l + r >> 1
    *  l + r + 1 >> 1
    * 
    * x
    */
    int x = a[l + r >> 1], i = l - 1, j = r + 1;
    while (i < j)
    {
        do i++; while (a[i] < x);
        do j--; while (a[j] > x);
        if (i < j) swap(a[i], a[j]);
    }

    /**
    * l, j li - 1
    * 
    * i  j
    * - i == j
    * - j < iji
    */
    quick_sort(a, l, j);
    quick_sort(a, j + 1, r);
}

int main(void)
{
    scanf("%d", &n); //scanfcin
    for (int i = 0; i < n; i++) scanf("%d", &a[i]);

    quick_sort(a, 0, n - 1);

    for (int i = 0; i < n; i++) printf("%d ", a[i]);

    return 0;
}
